package allconditions;

public class LessThenGreaterThen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=15;
		int b=20;
		if (a<b) {
			System.out.println("B is greater then a");
		}
		else {
			System.out.println("A is smaller then b");
		}
	}

}
